<?phpclass banwordClass extends InitFrm{	public function __construct()	{		parent::__construct();	}		public function __destruct()	{		parent::__destruct();	}		public function show($offset, $count, $data)	{		if ($count != -1)		{			$data_limit = ' LIMIT ' . $offset . ' , ' . $count;		}		$sql = 'SELECT * FROM ' . DB_PREFIX . 'banword WHERE 1';		if ($data && is_array($data))		{			$condition = $this->get_condition($data);		}		elseif ($data && is_string($data))		{			$condition = $data;		}		if ($condition) $sql .= $condition;		$sql .= ' ORDER BY id DESC';		if ($data_limit) $sql .= $data_limit;		$query = $this->db->query($sql);		$info = array();		while ($rows = $this->db->fetch_array($query))		{			$info[] = $rows;		}		return $info;	}		public function count($data)	{		$sql = 'SELECT COUNT(id) AS total FROM ' . DB_PREFIX . 'banword WHERE 1';		$condition = $this->get_condition($data);		$sql .= $condition;		return $this->db->query_first($sql);	}		public function detail($id)	{		$sql = 'SELECT * FROM ' . DB_PREFIX . 'banword WHERE id = ' . $id;		return $this->db->query_first($sql);	}		public function exists($data)	{		$sql = 'SELECT COUNT(id) AS total FROM ' . DB_PREFIX . 'banword WHERE 1';		if (is_int($data))		{			$sql .= ' AND id = ' . $data;		}		elseif (is_string($data))		{			$sql .= ' AND banname = "' . $data . '"';		}		return $this->db->query_first($sql);	}		public function create($data, $flag = 1)	{		if ($flag === 1)		{			$fields = array();			foreach($data as $k=>$v)			{				if (is_string($v))				{					$fields[] = $k . "='" . $v . "'";				}				elseif (is_int($v))				{					$fields[] = $k . '=' . $v;				}			}			$sql = 'INSERT INTO ' . DB_PREFIX . 'banword SET ' . implode(',', $fields);		}		elseif ($flag === 2)		{			$insertFields = array();			foreach ($data['fields'] as $v)			{				$insertFields[] = '`' . $v . '`';			}			$sql = 'INSERT INTO ' . DB_PREFIX . 'banword (' . implode(', ', $insertFields) . ') VALUES ' . $data['values'];		}		if ($sql) return $this->db->query($sql);		return false;	}		public function update($data, $id)	{		$fields = array();		foreach($data as $k=>$v)		{			if (is_string($v))			{				$fields[] = $k . "='" . $v . "'";			}			elseif (is_int($v))			{				$fields[] = $k . '=' . $v;			}		}		$sql = 'UPDATE ' . DB_PREFIX . 'banword SET ' . implode(',', $fields) . ' WHERE 1';		if (is_int($id))		{			$sql .= ' AND id = ' . $id;		}		elseif (is_string($id))		{			$sql .= ' AND id in (' . $id . ')';		}		return $this->db->query($sql);	}		public function delete($id)	{		$sql = 'DELETE FROM ' . DB_PREFIX . 'banword WHERE 1';		if (is_int($id))		{			$sql .= ' AND id = ' . $id;		}		elseif (is_string($id))		{			$sql .= ' AND id in (' . $id . ')';		}		return $this->db->query($sql);	}		public function emptyData($table)	{		$sql = 'TRUNCATE ' . DB_PREFIX . $table;		return $this->db->query($sql);	}		public function get_condition($data)	{		$condition = '';				//查询的关键字		if(isset($data['key']))		{			$keyword = htmlspecialchars(trim(urldecode($data['key'])));			$condition .= " AND banname LIKE '%" . $keyword . "%' ";		}		return $condition;	}}?>