<?phprequire_once './global.php';class team_node extends adminBase{	public function __construct()	{		parent::__construct();	}	public function __destruct()	{		parent::__destruct();	}		//获取分组节点	public function show()	{		$this->setXmlNode('team_types' , 'team_type');		foreach($this->settings['team_type'] as $k=>$v)		{			$result = array(				'id' => $k,				"name" => $v,				"fid" => 0,				"depth" => 1,				'attr' => $this->settings['team_type_attr'][$k],				'input_k' => '_type',				'is_last' => 1			);			$this->addItem($result);		}		$this->output();	}}$out = new team_node();$action = $_INPUT['a'];if(!method_exists($out, $action)){	$action = 'show';	}$out->$action();?>