{code}	$img = $v['index_url'] ? $v['index_url']['host'] . $v['index_url']['dir'] . '40x30/' . $v['index_url']['filepath'] . $v['index_url']['filename'] : '';{/code}	<div class="m2o-each m2o-flex m2o-flex-center" data-id="{$v['id']}" orderid="{$v['order_id']}">    <div class="m2o-item m2o-paixu">		<input type="checkbox" value="{$v['id']}" title="{$v['id']}" name="infolist[]" class="m2o-check" style="visibility: visible;">	</div>	<div class="m2o-item m2o-flex-one m2o-bt" title="标题">		 <div class="m2o-title-transition m2o-title-overflow">			 <a href="./run.php?mid={$_INPUT['mid']}&a=form&id={$v['id']}&infrm=1">				{if $img}<img style="margin-right:10px" src="{$img}" width="40" height="30">{/if}				<span>{$v['zh_name']}</span>			</a>		 </div>	</div>    <div class="m2o-item m2o-mark">{$v['catalog_field']}</div>    <div class="m2o-item m2o-sort">{$v['form_style_name']}</div>    <div class="m2o-item m2o-switch" _status="{$v['switch']}" >    	<div class="common-switch {if $v['switch']}common-switch-on{/if}">           <div class="switch-item switch-left" data-number="0"></div>           <div class="switch-slide"></div>           <div class="switch-item switch-right" data-number="100"></div>        </div>    </div>     <div class="m2o-item m2o-sort">{$v['form_sort_name']}</div>    <div class="m2o-item m2o-time">        <span class="name">{$v['user_name']}</span>        <span class="time">{$v['create_time']}</span>    </div>    <div class="m2o-item m2o-ibtn">    </div></div>