{template:./head}{css:manage}<script type="text/javascript">//<![CDATA[function change_state(state){	var uid = [];	$('input[name="selectMember[]"]').each(function() {		if ($(this).attr('checked'))		{			uid.push($(this).val());		}	});	location.href = 'activity.php?a=change_state&user_id=' + uid.join(",") + '&state=' + state + '&action_id=' + {$_INPUT['action_id']};}//]]>function setStar(state){	var uid = [];	$('input[name="selectMember[]"]').each(function() {		if ($(this).attr('checked'))		{			uid.push($(this).val());		}	});	location.href = 'activity.php?a=setStar&params=' + uid.join(",") + '&ctype=' + state + '&action_id=' + {$_INPUT['action_id']};}function setUserlevl(levl){	var uid = [];	$('input[name="selectMember[]"]').each(function() {		if ($(this).attr('checked'))		{			uid.push($(this).val());		}	});	location.href = 'activity.php?a=setUserlevl&params=' + uid.join(",")  + '&levl=' + levl + '&action_id=' + {$_INPUT['action_id']};}</script><div class="gong_main">	<div class="g_main_box">		<div class="g_main_box_l">			<ul>				{if $right}<li><a style="background:#fd8c02; color:#fff;"href="activitys.php?action_id={$_INPUT['action_id']}&a=update">设置</a></li>{/if}				<li><a style="">成员</a></li>				{if $right}<li><a style="background:#fd8c02; color:#fff;"href="activity.php?action_id={$_INPUT['action_id']}&a=setParams">参数设置</a></li>{/if}			</ul>		</div>		<div class="g_main_box_r">			<a href="activity.php?action_id={$_INPUT['action_id']}">返回活动</a>		</div>	</div>	<div class="g_main_con1">		<div class="g_main_left_line">			<div class="g_main_right_line">				<div class="chen_admin_box">					<h3>管理员</h3>					<ul>						{if $administor}						{foreach $administor as $v}						<li>						<!--判断角色加图标-->						<div class="role-icon">						     {if $v['levl']==1}						     <div class="chen_pic"></div>     <!--管理员图标-->						     <div class="star_pic"></div>     <!--明星图标-->						     <div class="member_pic"></div>   <!--成员图标-->						     <div class="creater_pic"></div>  <!--创建者图标-->						     <div class="uncheck_pic"></div>  <!--未审核成员图标-->						    {/if}						</div>												<a href="member.php?uid={$v['user_id']}" class="chen_imgpic">						{code}						$str = '';						if(is_array($user_info[$v['user_id']]['avatar']) && !empty($user_info[$v['user_id']]['avatar']['host']))						{							$str = $user_info[$v['user_id']]['avatar']['host'].$user_info[$v['user_id']]['avatar']['dir']."99x96/".$user_info[$v['user_id']]['avatar']['filepath'].$user_info[$v['user_id']]['avatar']['filename'];						}						else						{							$str = DEFAULT_AVATAR3;						}						{/code}						<img src="{$str}" width="99" height="96" /></a>							<div class="chen_admin">								<a href="member.php?uid={$v['user_id']}"><p style="border-bottom: 1px dashed #ccc;">								{if $v['levl']==1}管理员{else}创建者{/if}:<span style="color: #ff8b00;">{$user_info[$v['user_id']]['nick_name']}</span>								</p></a>								{if $right}								{if $v['levl']==1}								<p><a href="javascript:;" onclick="setUserlevl(2)">任命为创建者</a></p>								{else}								<p><a href="javascript:;" onclick="setUserlevl(1)">降为管理员</a></p>								{/if}								{if $v['apply_star']==0}								<p><a href="#">选为成员明星</a></p>								{else}								<p><a href="#">退出成员明星</a></p>								{/if}								<p><a href="#">降为普通成员</a></p>								<p><a href="javascript:;" onclick="change_state(3)"">加入黑名单</a></p>								{/if}							</div>						</li>						{/foreach}						{/if}					</ul>				</div>				<div class="main">					<div class="menu">						<span><a {if $state == 0}class="hover"{else}href="activity.php?action_id={$action_id}&a=getMemberByAid"{/if}>已审核成员</a>&nbsp;						{if $right}|&nbsp;<a {if $state == 1}class="hover"{else}href="activity.php?action_id={$action_id}&a=getMemberByAid&state=1"{/if}>待审核成员</a>{/if}</span>					</div>					<div class="content">						{if $members}						<ul>							{foreach $members as $v}							{code}							$str = '';							if(is_array($user_info[$v['user_id']]['avatar']) && !empty($user_info[$v['user_id']]['avatar']['host']))							{								$str = $user_info[$v['user_id']]['avatar']['host'].$user_info[$v['user_id']]['avatar']['dir']."99x96/".$user_info[$v['user_id']]['avatar']['filepath'].$user_info[$v['user_id']]['avatar']['filename'];							}							else							{								$str = DEFAULT_AVATAR3;							}							{/code}							<li class="shenhe-li"><a href="member.php?uid={$v['user_id']}">							<img src="{$str}" width="99" height="96" /></a>								<div class="chen_user">								<a href="member.php?uid={$v['user_id']}"><p>{$user_info[$v['user_id']]['nick_name']}</p></a>								{if $right}								<!-- <p> -->									<input name="selectMember[]" type="checkbox"										value="{$v['user_id']}" />								<!--  </p> -->								{/if}								</div>							</li> 							{/foreach}						</ul>						{else}						<p class="no_member">暂没有数据</p>						{/if}					</div>					<div style="padding: 10px;">						<span class="audit">{$pagelink}</span>						{if $right}							<a href="javascript:;">全选/不选</a>&nbsp;|&nbsp;<a							href="javascript:;" onclick="change_state(2)">审核</a>							&nbsp;|&nbsp;<a href="javascript:;" onclick="change_state(1)">打回</a>							&nbsp;|&nbsp;<a href="javascript:;" onclick="setUserlevl(2)">任命为创建者</a>							&nbsp;|&nbsp;<a href="javascript:;" onclick="setUserlevl(1)">任命为管理员</a>							&nbsp;|&nbsp;<a href="javascript:;" onclick="setUserlevl(0)">降为普通会员</a>							&nbsp;|&nbsp;<a href="javascript:;" onclick="change_state(3)">加入黑名单</a>							&nbsp;|&nbsp;<a href="javascript:;"  onclick="setStar(1)">选为成员明星</a>							&nbsp;|&nbsp;<a href="javascript:;" onclick="setStar(0)">退出成员明星</a>					    {/if}					</div>				</div>			</div>		</div>	</div></div>{template:./footer}