{template:./head}{js:qingao/group_groupcreate}{js:qingao/group_indexmap_b}{js:qingao/base}{js:qingao/action}	<h1 class="actiont action_h1"><span class='ks_start'><img src="{$RESOURCE_URL}qingao/{$activity_info['url']}" width='65px' height='65px'></span><a href="#" id="topic_name">{$activity_info['action_name']}</a></h1><!-- actiont2表示进行中, actiont3表示已结束--->		<div class="gshow_top"></div>		<div class="gshow_c clearfix">			<div class="atopic">			{code}					$lurl1 = $lurl2 = '';					$lurl1 .= isset($activity_info['action_img']['host']) ? $activity_info['action_img']['host'] :"";					$lurl1 .= isset($activity_info['action_img']['dir']) ? $activity_info['action_img']['dir'] :"";					$lurl2 .= isset($activity_info['action_img']['filepath']) ? $activity_info['action_img']['filepath'] :"";					$lurl2 .= isset($activity_info['action_img']['filename']) ? $activity_info['action_img']['filename'] :"";			{/code}				<img src="{$lurl1}626x290/{$lurl2}" width="626" height="290" />				<div class="asay">					<a href="javascript:;" class="awesay">大家说</a>				</div>				<div class="plugin_weibo" style="display:none"><!--我们说加类名 plugin_weibo_our-->					<div class="plugin_weibo_arrow"></div>					<div class="plugin_weibo_title">						<h3>行动微博动态</h3>						<a href="javascript:void(0);" class="plugin_weibo_close">x</a>					</div>					<div class="plugin_weibo_list">						{template:unit/activity_weibo}					</div>					<div class="plugin_weibo_item_more"><span>展开更多</span></div>					<div class="plugin_weibo_reply">						<div class="plugin_weibo_char"><!--<span>还可输入</span><span id="charNum" data-num="100">100</span>字--></div>						<form name="say_form" id="say_form" method="post" action="activity.php" onSubmit="return false;">							<textarea name="content" id="content" class="plugin_weibo_reply_cnt" id="msgtxt">#{$activity_info['action_name']}#</textarea>							<div class="plugin_weibo_reply_btn"><input type="submit" value="发布" /></div>							<div class="sys_mood"><a href="javascript:void(0);" ></a></div>							<input type="hidden" value="update_weibo" name="a"/>						</form>										</div>				</div><!-- plugin_weibo end-->			</div>			<aside class="atopic_aside" style="position:relative;">				<hgroup>				{if $activity_info['slogan']}					{$activity_info['slogan']}				{else}					快，给行动设置一个醒目的口号!				{/if}									</hgroup>				<div style="position:absolute; bottom:5px;">						<div class="atopic_tags">					{code}					if($activity_info['sign'])					{							$spce='';						foreach ($activity_info['sign'] as $sign)						{							echo $spce.'<a href="#">'.$sign['mark_name'].'</a>';							$spce =',';						}					}					else					{						echo "<a href=#>没有标签</a>";					}					{/code}					</div>					<div class="atopic_btn clearfix">						{if $times}							<a href="javascript:;" class="atopic_btn_join gray_btn">活动结束</a>						{else}							{if $activity_info['is_join'] == 4}							<a href="javascript:;" class="atopic_btn_join" id="join_action" aid="{$activity_info['id']}">参与行动</a>							{elseif $activity_info['is_join'] == 0 || $activity_info['is_join'] == 2}							<a href="javascript:;" class="atopic_btn_join gray_btn">已参与</a>							{elseif $activity_info['is_join'] == 1}							<a href="javascript:;" class="atopic_btn_join  gray_btn">审核中</a>							{/if}						{/if}							<a href="#" class="atopic_btn_love{if $activity_info['have']} gray_btn{/if}" id ="join_love" aid="{$activity_info['id']}" bid="{$activity_info['have']}">{if $activity_info['have'] > 0}取消关注{else}关注{/if}</a> 							{if $ower['id'] == $activity_info['user_id']}							<a href="activitys.php?action_id={$_INPUT['action_id']}&a=update" class="atopic_btn_love">管理</a> 							{/if}						</div>					<div class="atopic_count">					<span>{$activity_info['apply_num']}人报名</span><span class="yet_join">{$activity_info['yet_join']}人参加</span><span id="praisecount">{$praisecount}赞</span><span>{$activity_info['collect_num']}人关注</span>						<!-- <span>21分享</span> -->					</div>				</div>				<a class="atopic_digg_btn {if !$praise}atopic_btn_digg{else}atopic_btn_already_digg{/if}" aid="{$activity_info['id']}"></a>			</aside>		</div>		<div class="gshow_bottom"></div>	</section><!--展示区完-->	<section class="wrap clearfix">		<article class="gmain">			<div class="gmain_top"></div>			<div class="cmain clearfix">            	<div class="action_sum">					<div class="action_sum_title">						<h2>行动总结</h2>					</div>                    <div class="action_sum_wen">                    	<div class="summsry_left">							{$activity_info['summary']}                        </div>                        {if $connctuser['stars']}                    	<div class="summsry_right">                        	<div class="sum_ming"><h2>行动明星</h2></div>                            <div class="sum_list">                            	<ul>                            	{foreach $connctuser['stars']  as $k=>$user}                            	{code}								$avert = $url1 = $url2 =  '';								$url1 .= isset($user_info[$user['user_id']]['avatar']['host']) ? $user_info[$user['user_id']]['avatar']['host'] : "";								$url1 .= isset($user_info[$user['user_id']]['avatar']['dir']) ? $user_info[$user['user_id']]['avatar']['dir'] : "";								$url2 .= isset($user_info[$user['user_id']]['avatar']['filepath']) ? $user_info[$user['user_id']]['avatar']['filepath'] : "";								$url2 .= isset($user_info[$user['user_id']]['avatar']['filename']) ? $user_info[$user['user_id']]['avatar']['filename'] : "";								$avert = ($url1 && $url2) ? $url1."50x50/".$url2 : DEFAULT_AVATAR1;								{/code}									<li><a href="member.php?uid={$user['user_id']}"><img src="{$avert}" width="50" height="50" /></a><p><a href="member.php?uid={$user['user_id']}">{$user_info[$user['user_id']]['nick_name']}</a></p></li>								{/foreach}                                </ul>                            </div>                        </div>                        {/if}                    </div>                </div>				<!--行动总结 end-->                <div class="clear"></div>				<div class="action_moment">					<div class="action_title">						<h2>闪亮时刻</h2>						<h3><a href="#" class="tmore">浏览更多</a></h3>					</div>                    <div class="sum_shipin">                    	<img src="images/pic2.jpg">                    	<p>可以满足我的基本生活，能更好为这个城市作出贡献。</p>                    </div>                    <div class="moment_right">                    	<ul>                        	<li><a href="/"><img src="images/pic3.jpg"></a><p><a href="/">可以满足我的基本生活，能更好为这个城市作出贡献。</a></p></li>                        	<li><a href="/"><img src="images/pic3.jpg"></a><p><a href="/">可以满足我的基本生活，能更好为这个城市作出贡献。</a></p></li>                        	<li><a href="/"><img src="images/pic3.jpg"></a><p><a href="/">可以满足我的基本生活，能更好为这个城市作出贡献。</a></p></li>                        	<li><a href="/"><img src="images/pic3.jpg"></a><p><a href="/">可以满足我的基本生活，能更好为这个城市作出贡献。</a></p></li>                        </ul>                    </div>				</div>							</div><!--gtalks end-->            <div class="clear"></div>			<div class="gmain_bottom"></div>		</article>		<aside class="gaside">			<div class="gaside_top"></div>			<div class="gaside_m">			{if $group_infos}				<div class="a_group">					<h3><span class="title">关联圈子</span><a href="#" class="tmore">浏览全部</a></h3>					<ul class="clearfix">					{foreach $group_infos as $group}					{code}					if (empty($group['logo']))					{						$group['logo'] = DEFAULT_LOGO;					}					{/code}						<li><a href="group.php?group_id={$group['id']}"><img src="{if is_string($group['logo'])}{$group['logo']}{else}{$group['logo']['host']}{$group['logo']['dir']}64x64/{$group['logo']['filepath']}{$group['logo']['filename']}{/if}" width="64" height="64" /></a><p><a href="group.php?group_id={$v['id']}">{$group['name']}</a></p></li>					{/foreach}					</ul>				</div>			{/if}				{if $activity_info['yet_join']}				<div class="g_user">					<h3><span class="title">参与成员</span><a href="activity.php?action_id={$_INPUT['action_id']}&a=getMemberByAid" class="tmore">{$activity_info['yet_join']}</a></h3>					<ul class="clearfix">						{foreach $activity_info['apply_info'] as $user}						{code}						$avert = $url1 = $url2 =  '';						$url1 .= isset($user_info[$user['user_id']]['avatar']['host']) ? $user_info[$user['user_id']]['avatar']['host'] : "";						$url1 .= isset($user_info[$user['user_id']]['avatar']['dir']) ? $user_info[$user['user_id']]['avatar']['dir'] : "";						$url2 .= isset($user_info[$user['user_id']]['avatar']['filepath']) ? $user_info[$user['user_id']]['avatar']['filepath'] : "";						$url2 .= isset($user_info[$user['user_id']]['avatar']['filename']) ? $user_info[$user['user_id']]['avatar']['filename'] : "";						$avert = ($url1 && $url2) ? $url1."50x50/".$url2 : DEFAULT_AVATAR1;						{/code}						<li><a href="member.php?uid={$user['user_id']}"><img src="{$avert}" width="50" height="50" /></a><p><a href="member.php?uid={$user['user_id']}">{$user_info[$user['user_id']]['nick_name']}</a></p></li>						{/foreach}					</ul>				</div>				{/if}					</div>			<div class="gaside_bottom"></div>		</aside>	</section>	<script>	$(document).ready(function(){		$(".action_detail").tabs({'tabNav': '.act_det_tle ul',	'tabPanel': '.act_det_pane','active': 'current',});		window.onload = function(){            var zhong = $('.cmain');            var height = $('.gaside_m').outerHeight(true);            if(zhong.height() < height){                zhong.height(height);            }        };	});	</script>{template:./footer}