<div class="my_right">	<div class="my_r_top"></div>	<div class="my_r_mid">		<!--粉丝-->		<ul class="my_r_fs">			<li class="one"><p class="my_p_fs" id="followers_count">{$followers_count}</p><p>粉丝</p></li>			<li><p class="my_p_fs" id="attention_count">{$attention_count}</p><p>关注</p></li>			<li><p class="my_p_fs" id="activity_count">{$activity_count}</p><p>行动</p></li>		</ul>		<div class="clear"></div>		<!--列表-->		{if $friends}		<ul class="my_fs_list">		{foreach $friends as $k => $v}			{if !empty($v)}			{code}				if($v['filename'])				{					$avatar = $v['host'] . $v['dir'] . '50x50/' . $v['filepath'] . $v['filename'];				}				else				{					$avatar = DEFAULT_AVATAR1;				}				$userlink = "?uid=" . $v['id'];			{/code}			<li><a href="{$userlink}"><img src="{$avatar}"></a><p><a href="{$userlink}">{$v['username']}</a></p></li>			{/if}		{/foreach}		</ul>		{/if}		<!--我的兴趣-->		<div class="my_hobby">			<h2 class="my_hobby_h2">{if $owner == '我'}<span><a href="member.php?a=editsign">修改</a></span>{/if}<a href="#">{$owner}的兴趣</a></h2>			<div class="my_h_map">			{if $signs}				<ul>{code}$i=1;{/code}					{foreach $signs as $k=>$v}					<li><a href="#" class="abs_{code}echo $i%9;{/code}">{$v['mark_name']}</a></li>					{code}$i++;{/code}					{/foreach}				</ul>			{else}				{$owner}没有任何兴趣！			{/if}			</div>		</div>		{if $is_my_page}		<!--可能感兴趣的人-->		<div class="my_myhobby">			<h2 class="my_hobby_h2"><span><a href="javascript:void(0);" id="change_vip">换一换</a></span><a href="#">可能感兴趣的人</a></h2>			<div class="my_hr"></div><input id="vip_nums" type="hidden" name="vip_nums" value="{$vipTotal}" /><input id="uid" type="hidden" name="uid" value="{$uid}" /><input id="is_my_page" type="hidden" name="is_my_page" value="{$is_my_page}" />			<ul class="hobby_list" id="vip">			{template:./vipUser}			</ul>		</div>		<!--可能感兴趣的活动-->		{if $actions}		<div class="my_myhobby">			<h2 class="my_hobby_h2"><a href="#">可能感兴趣的活动</a></h2>			<div class="my_hr"></div>			<ul class="my_canlist">			{foreach $actions as $k=>$v}				<li>{code}							$url = unserialize($v['action_img']);								$url1 = $url2 =  '';							$url1 .= isset($url['host']) ? $url['host'] : "";							$url1 .= isset($url['dir']) ? $url['dir'] : "";							$url2 .= isset($url['filepath']) ? $url['filepath'] : "";							$url2 .= isset($url['filename']) ? $url['filename'] : "";							$img = (!empty($url1) || !empty($url2)) ? $url1."185x112/".$url2 : "img/my_img9.jpg";												{/code}					<a href="activity.php?action_id={$v['id']}"><img src="{$img}"></a>					<h4><a href="#">{code}echo hg_cutchars(strip_tags($v['action_name']), 12);{/code}</a></h4>					<p>{$v['collect_num']}人感兴趣/{$v['yet_join']}人参加</p>				</li>			{/foreach}			</ul>		</div>		{/if}	{/if}	</div>	<div class="my_r_btn"></div></div>