{template:./head}{css:manage}<div class="gong_main">	<div class="g_main_box">		<div class="g_main_box_l">			<ul>				<li><a href="manage.php?group_id={$group_id}">设置</a></li>				<li><a href="manage.php?a=member&group_id={$group_id}">成员</a></li>				<li><a style="background: #fd8c02; color: #fff; text-decoration:none; cursor:default;">公告</a></li>			</ul>		</div>		<div class="g_main_box_r">			<a href="group.php?group_id={$group_id}">返回讨论区首页</a>		</div>	</div>	<div class="g_main_con">		<div class="g_main_con2">			<div class="g_main_left_line">				<div class="g_main_right_line">				{if $method == 'notice'}					<h2><a href="#notice">发表公告</a></h2>					<ul>						<li class="g_id">ID</li>						<li class="g_biaoti">公告标题</li>						<li class="g_fabiao">是否发表</li>						<li class="g_shu">查看数</li>						<li class="g_ctime">创建时间</li>						<li class="g_chaozuo">操作</li>					</ul>					<div class="gong_main_list">					{if $notices}						{foreach $notices as $v}						<ul>							<li class="g_id1">{$v['id']}</li>							<li class="g_biaoti1">{$v['title']}</li>							<li class="g_fabiao1">{if $v['active']}是{else}否{/if}</li>							<li class="g_shu1">{$v['views']}</li>							<li class="g_ctime1">{code}echo hg_get_format_date($v['pub_date'], 2);{/code}</li>							<li class="g_chaozuo1"><a href="manage.php?a=detail_notice&group_id={$_INPUT['group_id']}&id={$v['id']}" target="_blank">预览</a>&nbsp;|&nbsp;<a href="manage.php?a=modify_notice&group_id={$_INPUT['group_id']}&id={$v['id']}">修改</a>&nbsp;|&nbsp;<a href="manage.php?a=del_notice&group_id={$_INPUT['group_id']}&id={$v['id']}">删除</a></li>						</ul>						{/if}						{$pagelink}					{else}					<p>暂没有数据</p>					{/if}					</div>					<div class="gong_loding"></div>					<div class="gong_fanhui">						<a href="#">返回</a>					</div>				{/if}					<div class="gong_kuaishu">						<a name="notice"></a>						<h3>{if $method == 'notice'}发布{elseif $method == 'modify_notice'}修改{/if}公告</h3>						<form action="manage.php" method="post" name="noticeForm">							<p><label>公告标题：</label><input type="text" name="notice_title" value="{$notice['title']}" size="80" /></p>							<p><label style="vertical-align: top;">公告内容：</label>{$editor}</p>							<p><label>是否发表：</label><input type="radio" name="notice_active" id="active1" value="1"{if $notice['active']} checked="checked"{/if} /><label for="active1">是</label>&nbsp;&nbsp;<input type="radio" name="notice_active" id="active2" value="0"{if !$notice['active']} checked="checked"{/if} /><label for="active2">否</label></p>							<p><input type="hidden" name="a" value="{$method}" /></p>							<p><input type="hidden" name="id" value="{$notice['id']}" /></p>							<p><input type="hidden" name="group_id" value="{$_INPUT['group_id']}" /></p>							<p><input type="submit" name="subBtn" class="gong_submit" value="{if $method=='notice'}发布{elseif $method=='modify_notice'}保存{/if}" /></p>						</form>					</div>				</div>			</div>		</div>	</div></div>{template:./footer}