{template:./head}{css:manage}<div class="gong_main">	<div class="g_main_box">		<div class="g_main_box_l">			<ul>				<li><a style="background:#fd8c02; color:#fff;" href="activitys.php?action_id={$_INPUT['action_id']}&a=update">设置</a></li>				<li><a style="background:#fd8c02; color:#fff;" href="activity.php?action_id={$_INPUT['action_id']}&a=getMemberByAid">成员</a></li>				<li><a >参数设置</a></li>			</ul>		</div>		<div class="g_main_box_r">			<a href="activity.php?action_id={$_INPUT['action_id']}">返回活动</a>		</div>	</div>	<div class="g_main_con">		<div class="g_main_con_set">			<div class="g_main_left_line">				<div class="g_main_right_line">					<form action="activity.php?a=doSetParams" method="post"						enctype="multipart/form-data" id="actionForm">						<p>							<label class="actionLabel">参数设置：</label>							<label class="actionLabel">管理员最大数：</label>							<div class="actionItem">								<input type="text" value="{$action_info['connection_user']}" name="q_connection_user" size="4" />							</div>							<span>&nbsp;&nbsp;&nbsp;不包含创建者（0为不限）</span>							<div class="clear"></div>						</p>						<p>							<label class="actionLabel">成员明星最大数：</label>							<div class="actionItem">								<input type="text" value="{$action_info['apply_star']}" name="q_apply_star" size="4" />							</div>							<span>&nbsp;&nbsp;&nbsp;不包含创建者（0为不限）</span>							<div class="clear"></div>						</p>						<p>							<label class="actionLabel">关联圈子最大数：</label>							<div class="actionItem">								<input type="text" value="{$action_info['connection_group']}" name="q_connection_group" size="4" />							</div>							<span>&nbsp;&nbsp;&nbsp;（0为不限）</span>							<div class="clear"></div>						</p>						<p>							<label class="actionLabel">行动总结：</label>							<div class="actionItem">								<textarea rows="10" cols="50" name="q_summary">{$action_info['summary']}</textarea>								<script type="text/javascript">								CKEDITOR.replace('summary', {									toolbar : 'Basic',									width : '419',							        height : '500',							    });							    </script>						    </div>						    <div class="clear"></div>						</p>						<input type="hidden" name="q_action_id" value="{$action_info['id']}"  />						<div class="add_group_btn">							<input type="submit" name="add_group_btn" value="保存" />						</div>					</form>				</div>			</div>		</div>	</div></div>{template:./footer}