{code}extract($formdata);{/code}<form method="post" class="columnForm">	<h3>{if $_INPUT['id']}编辑{else}添加{/if}栏目</h3>	<table width="100%" border="0" cellspacing="0" cellpadding="0">		<tr>			<td>所属栏目</td>			<td>				<select name="pid">					<option value="0">根级栏目</option>				</select>			</td>		</tr>		<tr>			<td>栏目名称</td>			<td>				<input type="text" name="col_name" value="{$name}" size="30" />			</td>		</tr>		<tr>			<td>栏目简介</td>			<td>				<textarea name="col_intro" cols="60" rows="4">{$content}</textarea>			</td>		</tr>		<tr>			<td>是否外链</td>			<td>				<input type="radio" name="outsideLink" class="n-h" id="notLink" value="0"{if intval($is_outlink) === 0} checked="checked"{/if} /><label for="notLink">否</label>				<input type="radio" name="outsideLink" class="n-h" id="isLink" value="1"{if intval($is_outlink) === 1}  checked="checked"{/if} /><label for="isLink">是</label>			</td>		</tr>		<tr id="columnLink"{if !$id || !$is_outlink}style="display:none;"{/if}>			<td>栏目链接</td>			<td>				<input type="text" name="col_link" value="{$linkurl}" size="40" />			</td>		</tr>		<tr>			<td colspan="2">				<input type="button" value="{if $_INPUT['id']}保存{else}添加{/if}" class="button_6_14" id="saveColumn" />&nbsp;&nbsp;<input type="button" value="取消" class="button_6_14 cancelBtn" />				<input type="hidden" name="site_id" value="{$_INPUT['sid']}" />				{if $id}				<input type="hidden" name="id" value="{$id}" />				{/if}			</td>		</tr>	</table></form>