{code}extract($formdata);$str = '';function listAll($arr, $orgid){	global $str, $_INPUT;	if ($arr && is_array($arr))	{		foreach($arr as $v)		{			if ($_INPUT['uid'] && $orgid == $v['id']) {				$str .= '<option value="'.$v['id'].'" selected="selected">'.sep($v['depath']).$v['name'].'</option>';			}else {				$str .= '<option value="'.$v['id'].'">'.sep($v['depath']).$v['name'].'</option>';			}			if ($v['sub']) {				listAll($v['sub'], $orgid);			}		}	}	return $str;}function sep($num){	$sep_str = '';	for ($i=0;$i<($num-1)*2;$i++)	{		$sep_str .= '&nbsp;&nbsp;';	}	return $sep_str;}{/code}<form action="?mid={$_INPUT['mid']}&a={if $id}updateUser{else}addUser{/if}" method="post" class="columnForm" id="userForm" enctype="multipart/form-data" target="innerFrame">	<h3>{if $_INPUT['uid']}编辑{else}添加{/if}用户</h3>	<table width="100%" border="0" cellspacing="0" cellpadding="0">		<tr>			<td>用户名称</td>			<td>				<input type="text" name="username" value="{$user_name}" size="30" />			</td>		</tr>		<tr>			<td>用户密码</td>			<td>				<input type="password" name="pwd" size="30" />{if $id}[不改变，就不用填写]{/if}			</td>		</tr>		<tr>			<td>用户描述</td>			<td>				<textarea name="intro" cols="60" rows="4">{$brief}</textarea>			</td>		</tr>		<tr>			<td>用户头像</td>			<td>				{if $avatar}				<img src="{code}echo hg_bulid_img($avatar, 50, 50);{/code}" width="50" height="50" />				{/if}				<input type="file" name="Filedata" />			</td>		</tr>		<tr>			<td>所属组织</td>			<td>				<select name="org_id">					<option value="0">选择组织</option>					{code}					if ($orgInfo) {						echo listAll($orgInfo[0], $org_id);					}					{/code}				</select>			</td>		</tr>		<tr>			<td colspan="2">				<input type="button" value="{if $_INPUT['uid']}保存{else}添加{/if}" class="button_6_14" id="saveUser" />&nbsp;&nbsp;<input type="button" value="取消" class="button_6_14 cancelBtn" />				<input type="hidden" name="c_id" value="{$_INPUT['cid']}" />				{if $id}				<input type="hidden" name="uid" value="{$id}" />				{/if}			</td>		</tr>	</table>	<iframe name="innerFrame" id="innerFrame" style="display:none;"></iframe></form>