<!-- 往期列表 -->{code}$list = $list[0];$epaper_info = $list['epaper_info'];unset($list['epaper_info']);//hg_pre($list);{/code}{code}if(!isset($_INPUT['date_search'])){    $_INPUT['date_search'] = 1;}if(!$_INPUT['status_show']){	$_INPUT['status_show']= -1;}$attr_date = array(	'class' => 'colonm down_list data_time',	'show' => 'colonm_show',	'width' => 104,/*列表宽度*/	'state' => 1,/*0--正常数据选择列表，1--日期选择*/	'is_sub'=> 0,);{/code}{template:head}{css:2013/list}{css:2013/iframe}{css:2013/button}{css:previous-list}{js:jqueryfn/jquery.tmpl.min}{js:2013/ajaxload_new}{js:2013/list}{js:common/ajax_upload}{js:epaper/epaper_list}<body><!-- 这一部分会被推进父层框架，成为检索条件和添加、配置按钮 --><div style="display:none">	{template:unit/epaper_search}	<div class="controll-area fr mt5" id="hg_page_menu" style="display:block">		<a class="add-button news mr10" href="run.php?mid={$_INPUT['mid']}&a=form&epaper_id={$_INPUT['epaper_id']}&epaper_name={$_INPUT['epaper_name']}&cur_stage={$_INPUT['cur_stage']}&cur_date={$_INPUT['cur_date']}&infrm=1" target="mainwin">新增期刊</a>	</div></div>	<div class="m2o-form wrap">	<div class="m2o-inner">		<div class="m2o-flex">			<form action="run.php?mid={$_INPUT['mid']}&a=update_epaper" method="post" class="epaperInfo">			   <aside class="epaper-info">				  <div class="info-item head">					 <a class="photo">				       {code}		               $picture = $epaper_info['picture']['host'].$epaper_info['picture']['dir'].$epaper_info['picture']['filepath'].$epaper_info['picture']['filename'];		               {/code}						  <img src="{$picture}" />					 </a>					 <input type="file" style="display:none" name="Filedata" class='headPic' multiple="multiple">				 </div>			     <div style="margin-top:70px;">				    <div class="info-item">					   <span class="title">名称：</span>					   <input type="text" value="{$epaper_info['name']}" name="title" class="name">					   <input type="hidden" name="epaper_id" value="{$epaper_info['id']}">					   <input type="hidden" name="old_name" value="{$epaper_info['name']}">				    </div>				 <div class="info-item type">					<span class="title">类型:</span>					{code}	                    $type_item_source = array(	                        'class'=> 'down_list list',	                        'show'=> 'type_show',	                        'width' => 106,/*列表宽度*/	                        'state'=> 	0, /*0--正常数据选择列表，1--日期选择*/	                        'is_sub'=>	1,	                    );	                    	                    	$type_default = $epaper_info['sort_id'] ? $epaper_info['sort_id'] :  -1;                        $_configs['date_select'][-1] = '选择类型';	                {/code}	                {template:form/search_source,type,$type_default,$_configs['date_select'],$type_item_source}					 </div>				 <div class="info-item">					<span class="title">初始期数：</span>					<input type="number" min='0' value="{$epaper_info['init_stage']}" name="init_stage" class="initial_stage"/>				 </div>				 <div class="info-item">					<span class="title">初始日期：</span>					<input type="text" value="{$epaper_info['init_time']}" name="init_time" class="date-picker" />				 </div>				 <div class="info-item">					<span class="title">出版社：</span>					<input type="text" value="{$epaper_info['pub_company']}" name='pub_company' />				 </div>				 <div class="info-item">					<span class="title">出版物号：</span>					<input type="text" value="{$epaper_info['pub_no']}" name='pub_no' />				 </div>				 <div class="info-item">					<span class="title">代号：</span>					<input type="text" value="{$epaper_info['code_name']}" name='code_name' />				 </div>				 <div class="info-item">					<span class="title">主办单位：</span>					<input type="text" value="{$epaper_info['sponsor']}" name='sponsor' />				 </div>			  </div>			      <img src="{$RESOURCE_URL}/loading2.gif" id="top-loading" style=" display: none;">			      <input type="submit" class="save-button" value="保存" />  				</aside>		</form>			<section class="epaper-list m2o-flex-one">				<!--  	<form method="post" action="" name="listform">-->					<div class="m2o-list">						<div class="m2o-title m2o-flex m2o-flex-center">							<div class="m2o-item m2o-paixu"></div>							<div class="m2o-item m2o-flex-one m2o-bt">期数</div>							<div class="m2o-item date">日期</div>							<div class="m2o-item num">叠/版</div>							<div class="m2o-item m2o-state">状态</div>							<div class="m2o-item m2o-time">创建人/时间</div>						</div>		           {if !$list}	                 <p id="emptyTip" style="color:#da2d2d;text-align:center;font-size:20px;line-height:50px;font-family:Microsoft YaHei;">没有您要找的内容！</p>	                 <script>hg_error_html('#emptyTip',1);</script>                   {else}						<div class="m2o-each-list">						{foreach $list as $k => $val}						{template:unit/previous_list}						{/foreach}							<!-- 							{if $comment}							{foreach $comment as $k => $epaper_info}								{template:unit/photo_comment}							{/foreach}							{else}							<p style="color:#da2d2d;text-align:center;font-size:20px;line-height:50px;font-family:Microsoft YaHei;margin:0 10px">没有您要找的内容！</p>							{/if}							 -->						</div>						{/if}						<div class="m2o-bottom  m2o-flex m2o-flex-center">							<div class="m2o-paixu">			                    <input type="checkbox" title="全选" name="checkall" class="checkAll"/>			                </div>					       	<div class="m2o-batch batch-audit" data-method="audit">					       		 <a class="batch-handle">审核</a>					       	</div>					       	<div class="m2o-batch batch-audit" data-method="audit">					       		<a class="batch-handle">打回</a>					       	</div>					       	<div class="m2o-batch batch-delete" data-method="delete">					            <a class="batch-handle">删除</a>					       	</div>					       	<div class="m2o-flex-one">{$pagelink}</div>						</div>		            	</div>		            <!--    </form>-->				</section>		</div>	</div>     <span class="result-tip"></span></div></body><script>(function($){	var data = $.globalListData = {code}echo $list ? json_encode($list) : '{}';{/code};	$.extend($.geach || ($.geach = {}), {		data : function(id){			var info;			$.each(data, function(i, n){				if(n['id'] == id){					info = {							id : n['id'],         					period_id : n['id'],                  			period_num : n['period_num'],                       		period_date : n['period_date']           			}             		return false;    			}      		 });   			return info;        }    });    var controll = {			delajax : function( ids, obj ,method ){				var url = './run.php?mid=' + gMid + '&a=' + method + '&ajax=1';				var data = {id : ids};				$.globalAjax( obj, function(){					return $.getJSON(url, data, function(data){						if( data['callback'] ){							eval( data['callback'] );							return;						}						var data = data[0];						if(data){							if(data == 0 || data == -1){								var tipParam = {									string : data == 0 ? '请先删除期刊下的新闻' : '您没有权限管理',									width : 200,									delay : 2000								};								obj.myTip(tipParam);								$('.confim-cancel').trigger('click');								$('.m2o-option-close').trigger('click');								return false;							}							if(data == 'success'){								obj.remove();							}						}					});				} );           },	};	$('.m2o-list').glist();    $('.m2o-each').geach({		'custom_delete' : true,		'deleteCallback' : function(event,param){			var target = $(event.currentTarget),				li = target.closest('.m2o-each'),				id = target.closest('.m2o-option').data('id');			controll.delajax( id, li ,'delete' );		},	});})(jQuery);</script><script type="text/x-jquery-tmpl" id="m2o-option-tpl"><div class="m2o-option" data-id="{{= id}}">    <div class="m2o-option-inner m2o-flex">        <div class="m2o-btns m2o-flex">			<div class="m2o-btn-area m2o-flex">				<a class="option-delete" data-method="delete">删除</a>				<a target="formwin" href="./run.php?a=relate_module_show&app_uniq=epaper&mod_uniq=article&mod_a=news_edit&epaper_id={$_INPUT['epaper_id']}&period_id={{= id}}&epaper_name={$_INPUT['epaper_name']}&cur_stage={{= period_num}}&cur_date={{= period_date}}&infrm=1">编辑新闻</a>				<a target="formwin" href="./run.php?a=relate_module_show&app_uniq=epaper&mod_uniq=article&mod_a=link_edit&epaper_id={$_INPUT['epaper_id']}&period_id={{= id}}&epaper_name={$_INPUT['epaper_name']}&cur_stage={{= period_num}}&cur_date={{= period_date}}&infrm=1">编辑链接</a>			</div>			<div class="m2o-option-line"></div>        </div>    </div>	<div class="m2o-option-confirm">			<p>确定要删除该内容吗？</p>			<div class="m2o-option-line"></div>			<div class="m2o-option-confim-btns">				<a class="confim-sure">确定</a>				<a class="confim-cancel cancel">取消</a>			</div>	</div>	<div class="m2o-option-close"></div></div></script>