<div class="m2o-each m2o-flex m2o-flex-center" data-id="{$v['id']}" orderid="{$v['order_id']}">    <div class="m2o-item m2o-paixu">		<input type="checkbox" value="{$v['id']}" title="{$v['id']}" name="infolist[]" class="m2o-check" style="visibility: visible;">	</div>	<div class="m2o-item m2o-flex-one m2o-bt overflow" title="{$v['title']}">		<a class="common-title" href="./run.php?mid={$_INPUT['mid']}&a=form&id={$v['id']}&infrm=1" >			<span style="max-width: 130px;" title="{$v['title']}">{$v['rname']}</span>		</a>	</div>	<div class="m2o-item m2o-mark">{if $v['issystem']}是{else}否{/if}</div>	<div class="m2o-item m2o-mark">{if $v['iscustom']}是{else}否{/if}</div>	<div class="m2o-item m2o-op overflow">{$v['operation']}</div>    <div class="m2o-item m2o-mark">{$v['cycletypename']}</div>    <div class="m2o-item m2o-sort">{if !$v['cycletype']}N/A{else}{code} if($v['rewardnum']) echo $v['rewardnum'];else echo '不限';{/code}{/if}</div>    <div class="m2o-item m2o-switch" _status="{$v['opened']}" >    	<div class="common-switch {if $v['opened']}common-switch-on{/if}">           <div class="switch-item switch-left" data-number="0"></div>           <div class="switch-slide"></div>           <div class="switch-item switch-right" data-number="100"></div>        </div>    </div>     <!-- <div class="m2o-item m2o-time">        <span class="name">{$v['user_name']}</span>        <span class="time">{$v['update_time']}</span>    </div> -->    <div class="m2o-item m2o-ibtn">    </div></div>