<div class="m2o-each m2o-flex m2o-flex-center" data-id="{$v['id']}" orderid="{$v['order_id']}">    <div class="m2o-item m2o-paixu">		 <input type="checkbox" value="{$v['id']}" title="{$v['id']}" name="infolist[]" class="m2o-check" style="visibility: visible;">	</div>	<div class="m2o-item m2o-flex-one m2o-bt" title="{$v['name']}">		<a class="common-title" href="./run.php?mid={$_INPUT['mid']}&a=form&id={$v['id']}&infrm=1" target="nodeFrame"  need-back>			<span class="m2o-item-bt">				<span {if $v['usernamecolor']}style="color:{$v['usernamecolor']}"{/if}>{$v['name']}</span>				{if $v['icon']}           			{code}$v['icon']=hg_fetchimgurl($v['icon']);{/code}		        	<img src="{$v['icon']}" style="width:16px;height:16px;margin-top: -12px;margin-left:-5px;" />		        			        {/if}			</span>		</a>	</div>	<div class="m2o-item m2o-mark w80">{if $v['issystem']}是{else}否{/if}</div>	<div class="m2o-item m2o-mark w200">{$v['showstar']['star']}</div>	<div class="m2o-item m2o-mark w200">		<div id="enable_{$v['id']}" {if $v['enable']}style="color:#66CD00;cursor:pointer;"{else}style="color:#CD0000;cursor:pointer;"{/if}  onclick="hg_enable_tpl({$v['id']});">		{if $v['enable']}启用{else}未启用{/if}		</div>	</div>    <div class="m2o-item m2o-mark">{$v['updatetype']}</div>    <div class="m2o-item m2o-sort">{$v['creditshigher']}</div>    <!--      <div class="m2o-item m2o-switch" _status="{$v['switch']}" >    	<div class="common-switch {if $v['switch']}common-switch-on{/if}">           <div class="switch-item switch-left" data-number="0"></div>           <div class="switch-slide"></div>           <div class="switch-item switch-right" data-number="100"></div>        </div>    </div>     -->    <div class="m2o-item m2o-sort">{$v['creditslower']}</div>    <!-- <div class="m2o-item m2o-time">        <span class="name">{$v['user_name']}</span>        <span class="time">{$v['update_time']}</span>    </div> -->    <div class="m2o-item m2o-ibtn">    </div></div>