{template:head}{css:news_add}<script type="text/javascript">//<![CDATA[$(function() {	$('#file_input').click(function() {		$('#f_file').click();			});	$('#repeatBtn').click(function() {		var content = '<div class="repeat_con"><input type="text" name="col_repeat_date" id="col_repeat_date" autocomplete="off" size="20" onfocus="WdatePicker({skin:\'whyGreen\',dateFmt:\'yyyy-MM-dd\'})" value="" />&nbsp;&nbsp;<input type="checkbox" name="periodic" class="n-h" />周期性节目<br /><br /><input type="text" name="start_time" id="start_time" autocomplete="off" size="12" onfocus="WdatePicker({skin:\'whyGreen\',dateFmt:\'HH:mm:ss\'})" />&nbsp;&nbsp;－&nbsp;&nbsp;<input type="text" name="end_time" id="end_time" autocomplete="off" size="12" onfocus="WdatePicker({skin:\'whyGreen\',dateFmt:\'HH:mm:ss\'})" /></div>';		$('#repeat_box').append(content);	});	$('#select_property').click(function() {		hg_show_dialog('属性选择');	});});function hg_show_dialog(titleText){	$('#auth_title').html(titleText);	if($('#add_auth').css('display')=='none')	{	   var conHtml = '<div id="select_property_box"><span id="11" class="selected" title="点击取消选择">主持人</span><span id="22" title="点击选择">编导</span></div>';	   $('#auth_form').html(conHtml);	   $('#add_auth').css({'display':'block'});	   $('#add_auth').animate({'right':'50%','margin-right':'-300px'},'normal',function(){		 hg_resize_nodeFrame();	   });	}	else	{		hg_closeAuth();	}}function hg_closeAuth(){	$('#add_auth').animate({'right':'120%'},'normal',function(){		$('#add_auth').css({'display':'none','right':'0'});		hg_resize_nodeFrame();	});}//]]></script><style type="text/css">.per {float:left; margin-right:20px; color:#7d7d7d;}.per p {margin-left:20px; margin-top:10px;}.single_upload{width:615px;height:100%;min-height:880px;border:1px solid #C8D4E0;box-shadow:0 0 8px #c6c6c5;-webkit-box-shadow:0 0 5px #c6c6c5;-o-box-shadow:0 0 5px #c6c6c5;-moz-box-shadow:0 0 5px #c6c6c5;position:absolute;top:0px;right:-320px;display:none;background:#fff;z-index:99999999999;}.single_upload .upload_form{margin:0 15px;height:auto;clear:both;background: url("{$RESOURCE_URL}dottedLine.png") repeat-x scroll 0 3px;padding:3px 0 0 0;}.single_upload h2{margin:0 15px;background: url("{$RESOURCE_URL}dottedLine.png") repeat-x scroll 0 bottom;height: 39px;padding: 13px 10px 0 15px;font-family: Microsoft YaHei;}.single_upload h2 span.b{width:20px;height:20px;background:url("{$RESOURCE_URL}bg-all.png") no-repeat -74px -96px;display:block;font-size:0;line-height:0;cursor:pointer;float:right;margin:5px 0 0 0;}.single_upload h2 span.b:hover{background-position:-94px -96px;}#add_auth {display:none;}#property_box {margin-left:58px; padding:20px 10px 10px 10px;}#select_property_box {padding:20px;}#select_property_box span {cursor:pointer; margin:10px; padding:5px 10px; background:#e6f1fa; color:#7B7B7B;}#select_property_box span.selected {background:#72a7e3; color:#FFF;}</style><div id="hg_page_menu" class="head_op"{if $_INPUT['infrm']} style="display:none"{/if}>	<a href="?mid={$_INPUT['mid']}{$_ext_link}" class="button_6"><strong>栏目管理</strong></a>	<a href="?mid={$_INPUT['mid']}&a=form{$_ext_link}" class="button_6"><strong>新增栏目</strong></a></div><div id="channel_form" style="margin-left:40%;"></div><div class="wrap clear">	<div class="ad_middle">		<form class="ad_form h_l" method="post" id="type_form">			<h2>类型设置</h2>			<ul class="form_ul">				<li class="i">					<div class="form_ul_div clear">						<span class="title">类型名称：</span><input type="text" name="type_name" value="" />					</div>				</li>				<li class="i">					<div class="form_ul_div clear">						<span class="title">拥有属性：</span>						<span class="file_input s" id="select_property">选择属性</span>						<div id="property_box">							<input type="checkbox" name="property_ids[]" value="11" class="n-h" /><label>主持人</label>							<input type="checkbox" name="property_ids[]" value="22" class="n-h" /><label>编导</label>						</div>					</div>				</li>			</ul>			<!-- 隐藏于表单-->			<input type="hidden" name="a" value="create" />			<input type="hidden" name="{$primary_key}" value="{$$primary_key}" />			<input type="hidden" name="referto" value="{$_INPUT['referto']}" />			<input type="hidden" name="infrm" value="{$_INPUT['infrm']}" />			<input type="hidden" name="property_ids" value="" />			<br/>			<input type="submit" id="submit_ok" name="sub" value="添加" class="button_6_14" />		</form>		<hr />		<form class="ad_form h_l" method="post" id="property_form">			<h2>属性设置</h2>			<ul class="form_ul">				<li class="i">					<div class="form_ul_div clear">						<span class="title">属性名称：</span><input type="text" name="property_name" value="" />					</div>				</li>				<li class="i">					<div class="form_ul_div clear">						<span class="title">字段名称：</span><input type="text" name="field_name" value="" />					</div>				</li>			</ul>			<!-- 隐藏于表单-->			<input type="hidden" name="a" value="create" />			<input type="hidden" name="{$primary_key}" value="{$$primary_key}" />			<input type="hidden" name="referto" value="{$_INPUT['referto']}" />			<input type="hidden" name="infrm" value="{$_INPUT['infrm']}" />			<br/>			<input type="submit" id="submit_ok" name="sub" value="添加" class="button_6_14" />		</form>	</div>	<div class="right_version">		<h2><a href="run.php?mid={$_INPUT['mid']}&infrm=1">返回前一页</a></h2>	</div>	<!-- 新增分类面板 开始--> 	<div id="add_auth" class="single_upload"> 		<h2><span class="b" onclick="hg_closeAuth();"></span><span id="auth_title"></span></h2> 		<div id="add_auth_tpl" class="add_collect_form"> 			<div class="collect_form_top info  clear" id="auth_form"></div> 		</div>	</div>	<!-- 新增分类面板结束--></div>{template:foot}