<li order_id="" _id="{$v[$primary_key]}" class="common-list-data clear"  id="r_{$v[$primary_key]}" name="{$v['order_id']}" >   <div class="common-list-left ">	  <div class="common-list-item paixu">	     <a class="lb" name="alist[]">		   <input type="checkbox" name="infolist[]" value="{$v[$primary_key]}" title="{$v[$primary_key]}" />	     </a>	  </div>   </div>   <div class="common-list-right">   		<div class="common-list-item wd100 news-ren open-close">   			<span>{$v['plat_name']}</span>   		</div>		<div class="common-list-item news-ren open-close" style="width:200px;">			 {if $v['expired']}		     <span onclick="hg_request_auth({$v['platId']},'{$v['uid']}','{$v['token']}');">授权已过期</span>		     {else}		     <span>{$v['expired_time']}</span>		     <span onclick="hg_request_auth({$v['platId']},'{$v['uid']}','{$v['token']}');" style="margin-left:5px;">重新授权</span>		     {/if}		</div>   		<div class="common-list-item wd150 news-ren open-close">		     <span class="common-name">{$v['user_name']}</span>		     <span class="common-time">{$v['create_time']}</span>		</div>	</div>   <div class="common-list-i" onclick="hg_show_opration_info({$v['id']});"></div>   {code}		$href = './run.php?mid='.$_INPUT['mid'].'&a=form&id='.$v['id'].'&infrm=1';	{/code}   <div class="common-list-biaoti min-wd">	    <div class="common-list-item biaoti-transition">	      <div class="common-list-overflow max-wd">	      	<a href="{$href}">	      		{code}	      			$avatar = '';	      			if($v['avatar'])	      			{	      				$avatar = $v['avatar']['host'] . $v['avatar']['dir'] . $v['avatar']['filepath'] . $v['avatar']['filename'];	      			}	      		{/code}		   		<img src="{$avatar}" style="width:40px;height:30px;" class="biaoti-img"/><span id="title_{$v['id']}" class="common-title m2o-common-title">{$v['name']}</span>			</a>		   </div>		</div>   </div></li>